			if(t.indexOf('#')!=-1) { t = t.split('#')[0]; }
			if(!t) { return d; }

			t = t.split('&');
			for(var i=0; i<t.length; i++) {
				var a = t[i].split('=');
				d[decodeURIComponent(a[0])] = decodeURIComponent(a[1]);
			}
